import { Component } from '@angular/core';
import { AppstateService } from '../services/appstate.service';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent {


  constructor(public appStateService: AppstateService){}

  totalProductsChecked() {
    return this.appStateService.products.filter(p => p.checked).length;
  }
}
<div class="card">
    <div class="card-body">
        <nav class="p-3">
            <ul class="nav nav-pills">
                <li class="border border-info p-1 m-1 bg-dark-subtle">
                    <label>Total products :{{appStateService.totalProducts}}</label>
                </li>
                <li class="border border-info p-1 m-1 bg-danger">
                    <label>Total Pages : {{appStateService.totalPages}}</label>
                </li>
                <li class="border border-info p-1 m-1 bg-success">
                    <label>Product Per Page : {{appStateService.pageSize}}</label>
                </li>
                <li class="border border-info p-1 m-1 bg-primary-subtle">
                    <label>Total Checked :{{totalProductsChecked()}}</label>
                </li>
            </ul>
        </nav>
    </div>
</div>
