import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Product } from '../model/product.model';
import { ProductService } from '../services/product.service';

@Component({
  selector: 'app-new-product',
  templateUrl: './new-product.component.html',
  styleUrls: ['./new-product.component.css']
})
export class NewProductComponent implements OnInit{

  public productForm!: FormGroup;

  constructor(private formBuilder:FormBuilder, private prodcutService:ProductService){}

  ngOnInit(): void {
   this.productForm = this.formBuilder.group({
    name: this.formBuilder.control('', [Validators.required]),
    price: this.formBuilder.control(0),
    checked: this.formBuilder.control(false)
   });
  }
  saveProduct() {
   let product:Product = this.productForm.value;
   this.prodcutService.saveProduct(product)
   .subscribe({
    next: data => {
      alert(JSON.stringify(data))
    },
    error: err => {
      console.log(err);
    }
   });
  }
}
<div class="p-3">
    <div class="card">
        <div class="card-body">
           <div class="row">
               <div class="col-md-6">
                    <form [formGroup]="productForm" (ngSubmit)="saveProduct()">
                        <div class="mb-3">
                            <label class="form-label">Name</label>
                            <input class="form-control" formControlName="name">
                        </div>
                        <div class="mb-3">
                            <label class="form-label">Price</label>
                            <input class="form-control" formControlName="price">
                        </div>
                        <div class="mb-3">
                            <label class="form-label">Checked</label>
                            <input type="checkbox" class="form-check-input" formControlName="checked">
                        </div>
                        <button [disabled]="productForm.invalid" class="btn btn-success">Save</button>
                    </form>
               </div> 
           </div>
        </div>
    </div>
</div>
